import "./editProfile.css";
import Topbar from "../../components/topbar/Topbar";
import Sidebar from "../../components/sidebar/Sidebar";
import { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

export default function EditProfile() {
  const PF = process.env.REACT_APP_PUBLIC_FOLDER;
  const username = useParams().username;
  const navigate = useNavigate();

  const [user, setUser] = useState({});
  const [formData, setFormData] = useState({
    profilePicture: "",
    coverPicture: "",
    desc: "",
    college: "",
    role: "",
    course: "",
  });

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get(
          `http://localhost:8800/api/users?username=${username}`
        );
        setUser(res.data);
        setFormData({
          profilePicture: res.data.profilePicture || "",
          coverPicture: res.data.coverPicture || "",
          desc: res.data.desc || "",
          college: res.data.college || "",
          role: res.data.role || "",
          course: res.data.course || "",
        });
      } catch (error) {
        console.error("Error fetching user:", error);
      }
    };
    fetchUser();
  }, [username]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
      const { name, files } = e.target;
      if (files[0]) {
          setFormData((prev) => ({ ...prev, [name]: files[0] }));
      }
  };
    
  //   const handleSubmit = async (e) => {
  //       e.preventDefault();
      
  //       const updatedData = {
  //         userId: user._id,  // Pass the userId directly in the JSON body
  //         desc: formData.desc,
  //         college: formData.college,
  //         role: formData.role,
  //         course: formData.course,
  //       };
      
  //       try {
  //         const res = await axios.put(
  //           `http://localhost:8800/api/users/${user._id}`,
  //           updatedData,  // Send data as JSON instead of FormData
  //           {
  //             headers: {
  //               "Content-Type": "application/json",  // Specify JSON content type
  //             },
  //           }
  //         );
  //         alert("Profile updated successfully!");
  //         navigate(`/profile/${username}`);
  //       } catch (error) {
  //         console.error("Error updating profile:", error);
  //         alert("Failed to update profile.");
  //       }
  // };
  
  const handleProfilePictureSubmit = async () => {
    if (formData.profilePicture) {
        const data = new FormData();
        const fileName = Date.now() + formData.profilePicture.name;
        data.append("file", formData.profilePicture);

        try {
            const res = await axios.post("http://localhost:8800/api/upload", data);
            setFormData((prev) => ({
                ...prev,
                profilePicture: res.data.fileName, // Save the file name returned from the backend
            }));
            alert("Profile picture uploaded successfully!");
        } catch (err) {
            console.error("Error uploading profile picture:", err);
        }
    }
};

const handleCoverPictureSubmit = async () => {
    if (formData.coverPicture) {
        const data = new FormData();
        const fileName = Date.now() + formData.coverPicture.name;
        data.append("file", formData.coverPicture);

        try {
            const res = await axios.post("http://localhost:8800/api/upload", data);
            setFormData((prev) => ({
                ...prev,
                coverPicture: res.data.fileName, // Save the file name returned from the backend
            }));
            alert("Cover picture uploaded successfully!");
        } catch (err) {
            console.error("Error uploading cover picture:", err);
        }
    }
};
        
  return (
      <>
      <Topbar />
      <div className="editProfile">
        <Sidebar />
        <div className="editProfileWrapper">
          <h2>Edit Profile</h2>
          <form className="editProfileForm" onSubmit={handleSubmit}>
            <label>Profile Picture</label>
            <input type="file" name="profilePicture" onChange={handleFileChange} />
            <button type="button" onClick={handleProfilePictureSubmit}>
              Upload Profile Picture
            </button>

            <label>Cover Picture</label>
            <input type="file" name="coverPicture" onChange={handleFileChange} />
            <button type="button" onClick={handleCoverPictureSubmit}>
              Upload Cover Picture
            </button>

            <label>Description</label>
            <textarea
              name="desc"
              value={formData.desc}
              onChange={handleInputChange}
              placeholder="Write about yourself..."
            />

            <label>College</label>
            <input
              type="text"
              name="college"
              value={formData.college}
              onChange={handleInputChange}
              placeholder="Enter your college"
            />

            <label>Role</label>
            <input
              type="text"
              name="role"
              value={formData.role}
              onChange={handleInputChange}
              placeholder="Enter your role"
            />

            <label>Course</label>
            <input
              type="text"
              name="course"
              value={formData.course}
              onChange={handleInputChange}
              placeholder="Enter your course"
            />

            <button type="submit">Save Changes</button>
          </form>
        </div>
      </div>
    </>
  );
}
