import "./rightbar.css";
import { Users } from "../../dummyData";
import Online from "../online/Online";
import { useContext, useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import { Add, Remove } from "@mui/icons-material";

export default function Rightbar({ user }) {
  const PF = process.env.REACT_APP_PUBLIC_FOLDER;
  const [friends, setFriends] = useState([]);
  const { user: currentUser, dispatch } = useContext(AuthContext);
  const [followed, setFollowed] = useState(false);

  useEffect(() => {
    setFollowed(currentUser.following.includes(user?._id));
  }, [currentUser, user])

  useEffect(() => {
    if (!user || !user._id) {
      console.error("User ID is not available");
      return;
    }
    const getFriends = async () => {
      try {
        const friendList = await axios.get("http://localhost:8800/api/users/friends/" + user._id)
        setFriends(friendList.data);
      } catch (err) {
        console.log(err);
      }
    };
    getFriends();
  }, [user]);

  const handleClick = async () => {
    try {
      if (followed) {
        await axios.put("http://localhost:8800/api/users/" + user._id + "/unfollow", { userId: currentUser._id })
        dispatch({type: "UNFOLLOW", payload:user._id})
      } else {
        await axios.put("http://localhost:8800/api/users/" + user._id + "/follow", { userId: currentUser._id })
        dispatch({type: "FOLLOW", payload:user._id})
      }
      setFollowed(!followed);
    } catch (err) {
      console.log(err);
    }
  };



  const HomeRightbar = () => {
    return (
      <>
        <div className="birthdayConatainer">
          <img src="assets/gift.png" alt="" className="birthdayImage" />
          <span className="birthdayText">
            {" "}
            <b>Nabh Gupta</b> and <b>3 other friends</b> have birthday today.
          </span>
        </div>
        <img className="rightbarAd" src="assets/NewPoster.jpg" alt="" />
        <h4 className="rightbarTitle">Online Friends</h4>
        <ul className="rightbarFriendList">
        {onlineFriends.length > 0 ? (
          onlineFriends.map((u) => <Online key={u._id} user={u} />)
        ) : (
          <p>No friends online</p>
        )}
      </ul>
      </>
    );
  };

  const ProfileRightbar = () => {
    return (
      <>
        {user.username !== currentUser.username && (
          <button className="rightbarFollowButton" onClick={handleClick}>
            {followed ? "unfollow" : "Follow"}
            {followed ? <Remove /> : <Add />}
          </button>
        )}
        <h4 className="rightbarTitle">User information</h4>
          <div className="rightbarInfoItem">
            <span className="rightbarInfoKey">College:</span>
            <span className="rightbarInfoValue">{ user.college || "-"}</span>
          </div>
          <div className="rightbarInfoItem">
            <span className="rightbarInfoKey">Role:</span>
            <span className="rightbarInfoValue">{ user.role || "-"}</span>
          </div>
          <div className="rightbarInfoItem">
            <span className="rightbarInfoKey">Course:</span>
            <span className="rightbarInfoValue">{ user.course || "-"}</span>
          </div>
        <div className="rightbarInfo">
          <div className="rightbarInfoItem">
            <span className="rightbarInfoKey">City:</span>
            <span className="rightbarInfoValue">{ user.city || "-"}</span>
          </div>
        </div>
        <h4 className="rightbarTitle">User friends</h4>
        <div className="rightbarFollowings">
          {friends.map((friend) => (
            <Link to={"/profile/" + friend.username} style={{textDecoration: "none"}} key={friend._id}>
            <div className="rightbarFollowing">
            <img src={friend.profilePicture ? PF + friend.profilePicture : PF + "person/noAvatar.png"} className="rightbarFollowingImage" alt="" />
              <span className="rightbarFollowingName"> { friend.username }</span>
          </div>
            </Link>
          ))}    
        </div>
      </>
    );
  };
  return (
    <div className="rigthbar">
      <div className="rightbarWrapper">
        { user ? <ProfileRightbar /> : <HomeRightbar /> }
      </div>
    </div>
  );
}
