import "./editProfile.css";
import Topbar from "../../components/topbar/Topbar";
import Sidebar from "../../components/sidebar/Sidebar";
import { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

export default function EditProfile() {
  const PF = process.env.REACT_APP_PUBLIC_FOLDER;
  const username = useParams().username;
  const navigate = useNavigate();

  const [user, setUser] = useState({});
  const [formData, setFormData] = useState({
    profilePicture: "",
    coverPicture: "",
    desc: "",
    college: "",
    role: "",
    course: "",
  });

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get(
          `http://localhost:8800/api/users?username=${username}`
        );
        setUser(res.data);
        setFormData({
          profilePicture: res.data.profilePicture || "",
          coverPicture: res.data.coverPicture || "",
          desc: res.data.desc || "",
          college: res.data.college || "",
          role: res.data.role || "",
          course: res.data.course || "",
        });
      } catch (error) {
        console.error("Error fetching user:", error);
      }
    };
    fetchUser();
  }, [username]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
      const { name, files } = e.target;
      if (files[0]) {
          setFormData((prev) => ({ ...prev, [name]: files[0] }));  
      }
  };

//   const handleSubmit = async (e) => {
//     e.preventDefault();

//     const updatedData = new FormData();
//     updatedData.append("userId", user._id); // Include userId for authorization
    
//     updatedData.append("profilePicture", formData.profilePicture);
//     updatedData.append("coverPicture", formData.coverPicture);
//     updatedData.append("desc", formData.desc);
//     updatedData.append("college", formData.college);
//     updatedData.append("role", formData.role);
//     updatedData.append("course", formData.course);

//     try {
//       await axios.put(`http://localhost:8800/api/users/${user._id}`, updatedData, {
//         headers: {
//           "Content-Type": "multipart/form-data",
//         },
//       });
//       alert("Profile updated successfully!");
//       navigate(`/profile/${username}`);
//     } catch (error) {
//       console.error("Error updating profile:", error);
//       alert("Failed to update profile.");
//     }
    //   };
    
    const handleSubmit = async (e) => {
        e.preventDefault();
      
        const updatedData = {
          userId: user._id,  // Pass the userId directly in the JSON body
          desc: formData.desc,
          college: formData.college,
          role: formData.role,
          course: formData.course,
        };
      
        try {
          const res = await axios.put(
            `http://localhost:8800/api/users/${user._id}`,
            updatedData,  // Send data as JSON instead of FormData
            {
              headers: {
                "Content-Type": "application/json",  // Specify JSON content type
              },
            }
          );
          alert("Profile updated successfully!");
          navigate(`/profile/${username}`);
        } catch (error) {
          console.error("Error updating profile:", error);
          alert("Failed to update profile.");
        }
    };
    
    // Handle Profile Picture Update
  const handleProfilePictureSubmit = async (e) => {
    e.preventDefault();
    const formDataProfile = new FormData();
    formDataProfile.append("userId", user._id);
    formDataProfile.append("file", formData.profilePicture);

    try {
      await axios.post(
        `http://localhost:8800/api/users/profilePicture`,  // Separate route for profile picture
        formDataProfile,
        {
          headers: {
            "Content-Type": "multipart/form-data",  // Specify multipart form data
          },
        }
      );
      alert("Profile picture updated successfully!");
      navigate(`/profile/${username}`);
    } catch (error) {
      console.error("Error updating profile picture:", error);
      alert("Failed to update profile picture.");
    }
  };

  // Handle Cover Picture Update
  const handleCoverPictureSubmit = async (e) => {
    e.preventDefault();
    const formDataCover = new FormData();
    formDataCover.append("userId", user._id);
    formDataCover.append("file", formData.coverPicture);

    try {
      await axios.post(
        `http://localhost:8800/api/users/coverPicture`,  // Separate route for cover picture
        formDataCover,
        {
          headers: {
            "Content-Type": "multipart/form-data",  // Specify multipart form data
          },
        }
      );
      alert("Cover picture updated successfully!");
      navigate(`/profile/${username}`);
    } catch (error) {
      console.error("Error updating cover picture:", error);
      alert("Failed to update cover picture.");
    }
  };

      
  return (
    // <>
    //   <Topbar />
    //   <div className="editProfile">
    //     <Sidebar />
    //     <div className="editProfileWrapper">
    //       <h2>Edit Profile</h2>
    //       <form className="editProfileForm" onSubmit={handleSubmit}>
    //         <label>Profile Picture</label>
    //         <input type="file" name="profilePicture" onChange={handleFileChange} />

    //         <label>Cover Picture</label>
    //         <input type="file" name="coverPicture" onChange={handleFileChange} />

    //         <label>Description</label>
    //         <textarea
    //           name="desc"
    //           value={formData.desc}
    //           onChange={handleInputChange}
    //           placeholder="Write about yourself..."
    //         />

    //         <label>College</label>
    //         <input
    //           type="text"
    //           name="college"
    //           value={formData.college}
    //           onChange={handleInputChange}
    //           placeholder="Enter your college"
    //         />

    //         <label>Role</label>
    //         <input
    //           type="text"
    //           name="role"
    //           value={formData.role}
    //           onChange={handleInputChange}
    //           placeholder="Enter your role"
    //         />

    //         <label>Course</label>
    //         <input
    //           type="text"
    //           name="course"
    //           value={formData.course}
    //           onChange={handleInputChange}
    //           placeholder="Enter your course"
    //         />

    //         <button type="submit">Save Changes</button>
    //       </form>
    //     </div>
    //   </div>
      // </>
      <>
      <Topbar />
      <div className="editProfile">
        <Sidebar />
        <div className="editProfileWrapper">
          <h2>Edit Profile</h2>
          <form className="editProfileForm" onSubmit={handleSubmit}>
            <label>Profile Picture</label>
            <input type="file" name="profilePicture" onChange={handleFileChange} />
            <button type="button" onClick={handleProfilePictureSubmit}>
              Upload Profile Picture
            </button>

            <label>Cover Picture</label>
            <input type="file" name="coverPicture" onChange={handleFileChange} />
            <button type="button" onClick={handleCoverPictureSubmit}>
              Upload Cover Picture
            </button>

            <label>Description</label>
            <textarea
              name="desc"
              value={formData.desc}
              onChange={handleInputChange}
              placeholder="Write about yourself..."
            />

            <label>College</label>
            <input
              type="text"
              name="college"
              value={formData.college}
              onChange={handleInputChange}
              placeholder="Enter your college"
            />

            <label>Role</label>
            <input
              type="text"
              name="role"
              value={formData.role}
              onChange={handleInputChange}
              placeholder="Enter your role"
            />

            <label>Course</label>
            <input
              type="text"
              name="course"
              value={formData.course}
              onChange={handleInputChange}
              placeholder="Enter your course"
            />

            <button type="submit">Save Changes</button>
          </form>
        </div>
      </div>
    </>
  );
}
