import { useState } from 'react';
import Topbar from "../../components/topbar/Topbar"; // Import IntelliConnect's top bar
import '@chatscope/chat-ui-kit-styles/dist/default/styles.min.css';
import { MainContainer, ChatContainer, MessageList, Message, MessageInput, TypingIndicator } from '@chatscope/chat-ui-kit-react';
import "./chatBot.css";

const API_KEY = "YOUR_API_KEY";

export default function ChatBot() {
  const [messages, setMessages] = useState([
    {
      message: "Hello, I'm IntelliConnect! Ask me anything!",
      sentTime: "just now",
      sender: "Gemini"
    }
  ]);
  const [isTyping, setIsTyping] = useState(false);

  const handleSend = async (message) => {
    const newMessage = {
      message,
      direction: 'outgoing',
      sender: "user"
    };

    setMessages([...messages, newMessage]);
    setIsTyping(true);
    await processMessageToGemini(message);
  };

  async function processMessageToGemini(message) {
    const apiRequestBody = {
      contents: [{ parts: [{ text: message }] }]
    };

    try {
      const response = await fetch(
        `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent?key=${API_KEY}`,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(apiRequestBody)
        }
      );

      const data = await response.json();
      setMessages(prevMessages => [...prevMessages, {
        message: data.candidates[0].content.parts[0].text,
        sender: "Gemini"
      }]);
      setIsTyping(false);
    } catch (error) {
      console.error("Error:", error);
      setIsTyping(false);
    }
  }

  return (
    <div className="chatbot-page">
      <Topbar /> {/* IntelliConnect Top Bar */}
      <div className="chatbot-container">
        <MainContainer>
          <ChatContainer>
            <MessageList 
              scrollBehavior="smooth" 
              typingIndicator={isTyping ? <TypingIndicator content="IntelliConnect is typing..." /> : null}
            >
              {messages.map((message, i) => <Message key={i} model={message} />)}
            </MessageList>
            <MessageInput placeholder="Type message here..." onSend={handleSend} />        
          </ChatContainer>
        </MainContainer>
      </div>
    </div>
  );
}
