import "./profile.css";
import Topbar from "../../components/topbar/Topbar";
import Sidebar from "../../components/sidebar/Sidebar";
import Feed from "../../components/feed/Feed";
import Rightbar from "../../components/rightbar/Rightbar";
import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function Profile() {
  const PF = process.env.REACT_APP_PUBLIC_FOLDER;
  const [user, setUser] = useState({})
  const username = useParams().username;
    const [desc, setDesc] = useState(user.desc || "");
  const [college, setCollege] = useState(user.college || "");
  const [role, setRole] = useState(user.role || "");
  const [city, setCity] = useState(user.city || "");
  const [course, setCourse] = useState(user.course || "");
  const [file, setFile] = useState(null);
  const [coverFile, setCoverFile] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get(`http://localhost:8800/api/users?username=${username}`);
        setUser(res.data);
        setDesc(res.data.desc);
        setCollege(res.data.college);
        setRole(res.data.role);
        setCity(res.data.city);
        setCourse(res.data.course);

      } catch (error) {
        console.error("Error fetching user:", error);
      }
    };
    fetchUser();
  }, [username]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleCoverFileChange = (e) => {
    setCoverFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const updatedUser = {
      userId: user._id,
      desc,
      college,
      role,
      city,
      course,
    };

    if (file) {
      const formData = new FormData();
      const fileName = Date.now() + file.name;
      formData.append("file", file);
      formData.append("name", fileName);

      try {
        await axios.post("http://localhost:8800/api/upload", formData);
        updatedUser.profilePicture = fileName;
      } catch (err) {
        console.log("Error uploading file:", err);
      }
    }

    if (coverFile) {
      const coverFormData = new FormData();
      const coverFileName = Date.now() + coverFile.name;
      coverFormData.append("file", coverFile);
      coverFormData.append("name", coverFileName);

      try {
        await axios.post("http://localhost:8800/api/upload", coverFormData);
        updatedUser.coverPicture = coverFileName;
      } catch (err) {
        console.log("Error uploading cover picture:", err);
      }
    }

    try {
      await axios.put(`http://localhost:8800/api/users/${user._id}`, updatedUser);
      setUser({ ...user, ...updatedUser });
    } catch (err) {
      console.log("Error updating user:", err);
    }
  };

  return (
    <>
      <Topbar />
      <div className="profile">
        <Sidebar />
        <div className="profileRight">
          <div className="profileRightTop">
            <div className="profileCover">
              <img className="profileCoverImage" src={ user.coverPicture ? PF + user.coverPicture : PF+"person/noCover.png"}  alt="" />
              <img className="profileUserImage" src= { user.profilePicture ? PF + user.profilePicture : PF+"person/noAvatar.png"} alt="" />
            </div>
            <div className="profileInfo">
              <h4 className="profileInfoName">{user.username}</h4>
              <span className="profileInfoDesc">{user.desc}</span>
            </div>
          </div>
          <div className="profileRightBottom">
            <Feed username={ username } />
            <Rightbar user={user} />
          </div>
        </div>
      </div>
    </>
  );
}
