import { useEffect, useState } from "react";
import "./chatOnline.css";
import axios from "axios";

export default function ChatOnline({ onlineUsers, currentId, setCurrentChat }) {

  const [friends, setFriends] = useState([]);
  const [onlinefriends, setOnlineFriends] = useState([]);
  const PF = process.env.REACT_APP_PUBLIC_FOLDER;

  useEffect(() => {
    if (!currentId) {
        console.error("currentId is undefined or null");
        return;
    }

    const getFriends = async () => {
        try {
            const res = await axios.get("/users/friends/" + currentId);
            setFriends(res.data);
        } catch (err) {
            console.error("Error fetching friends:", err.message);
        }
    };

    getFriends();
}, [currentId]);

  useEffect(() => {
    setOnlineFriends(friends.filter((f) => onlineUsers.includes(f._id)));
  }, [friends, onlineUsers]);
  

  return (
    <div className="chatOnline">
      {onlinefriends.map((o) => (
        
        <div className="chatOnlineFriend" onClick={()=>{handleClick()}}>
        <div className="chatOnlineImageContainer">
            <img className="chatOnlineImage" src={o?.profilePicture
              ? PF + o?.profilePicture
              : PF + "perdon/noAvatar.png"
            } alt="" />
          <div className="chatOnlineBadge"></div>
        </div>
          <span className="chatOnlineName">{o.username }</span>
      </div>
      ))}
    </div>
  );
}
