import "./editProfile.css";
import Topbar from "../../components/topbar/Topbar";
import Sidebar from "../../components/sidebar/Sidebar";
import { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

export default function EditProfile() {
  const PF = process.env.REACT_APP_PUBLIC_FOLDER;
  const username = useParams().username;
  const navigate = useNavigate();

  const [user, setUser] = useState({});
  const [formData, setFormData] = useState({
    profilePicture: "",
    coverPicture: "",
    desc: "",
    college: "",
    role: "",
    course: "",
  });

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get(
          `http://localhost:8800/api/users?username=${username}`
        );
        setUser(res.data);
        setFormData({
          profilePicture: res.data.profilePicture || "",
          coverPicture: res.data.coverPicture || "",
          desc: res.data.desc || "",
          college: res.data.college || "",
          role: res.data.role || "",
          course: res.data.course || "",
        });
      } catch (error) {
        console.error("Error fetching user:", error);
      }
    };
    fetchUser();
  }, [username]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    if (files[0]) {
      setFormData((prev) => ({ ...prev, [name]: files[0] }));
    }
  };

  // Combined handle submit for profile and cover picture + user details
  const handleSubmit = async (e) => {
    e.preventDefault();

    const updatedData = {
      userId: user._id,  // Pass the userId directly in the JSON body
      desc: formData.desc,
      college: formData.college,
      role: formData.role,
      course: formData.course,
    };

    // Profile Picture Upload
    if (formData.profilePicture) {
      const formDataProfile = new FormData();
      formDataProfile.append("file", formData.profilePicture);

      try {
        const profilePictureRes = await axios.post(
          `http://localhost:8800/api/users/profilePicture`,  // Separate route for profile picture
          formDataProfile,
          {
            headers: {
              "Content-Type": "multipart/form-data",  // Specify multipart form data
            },
          }
        );
        updatedData.profilePicture = profilePictureRes.data.file; // Store the uploaded file name in updatedData
      } catch (error) {
        console.error("Error uploading profile picture:", error);
        alert("Failed to upload profile picture.");
      }
    }

    // Cover Picture Upload
    if (formData.coverPicture) {
      const formDataCover = new FormData();
      formDataCover.append("file", formData.coverPicture);

      try {
        const coverPictureRes = await axios.post(
          `http://localhost:8800/api/users/coverPicture`,  // Separate route for cover picture
          formDataCover,
          {
            headers: {
              "Content-Type": "multipart/form-data",  // Specify multipart form data
            },
          }
        );
        updatedData.coverPicture = coverPictureRes.data.file; // Store the uploaded file name in updatedData
      } catch (error) {
        console.error("Error uploading cover picture:", error);
        alert("Failed to upload cover picture.");
      }
    }

    // Update User Details
    try {
      await axios.put(
        `http://localhost:8800/api/users/${user._id}`,
        updatedData,
        {
          headers: {
            "Content-Type": "application/json",  // Specify JSON content type
          },
        }
      );
      alert("Profile updated successfully!");
      navigate(`/profile/${username}`);
    } catch (error) {
      console.error("Error updating profile:", error);
      alert("Failed to update profile.");
    }
  };

  return (
    <>
      <Topbar />
      <div className="editProfile">
        <Sidebar />
        <div className="editProfileWrapper">
          <h2>Edit Profile</h2>
          <form className="editProfileForm" onSubmit={handleSubmit}>
            <label>Profile Picture</label>
            <input type="file" name="profilePicture" onChange={handleFileChange} />
            
            <label>Cover Picture</label>
            <input type="file" name="coverPicture" onChange={handleFileChange} />

            <label>Description</label>
            <textarea
              name="desc"
              value={formData.desc}
              onChange={handleInputChange}
              placeholder="Write about yourself..."
            />

            <label>College</label>
            <input
              type="text"
              name="college"
              value={formData.college}
              onChange={handleInputChange}
              placeholder="Enter your college"
            />

            <label>Role</label>
            <input
              type="text"
              name="role"
              value={formData.role}
              onChange={handleInputChange}
              placeholder="Enter your role"
            />

            <label>Course</label>
            <input
              type="text"
              name="course"
              value={formData.course}
              onChange={handleInputChange}
              placeholder="Enter your course"
            />

            <button type="submit">Save Changes</button>
          </form>
        </div>
      </div>
    </>
  );
}
